### MAIN FUNCTIONS

def get_amount_out_v3(amount_in, token_in, token_out, fee):
    token_in_checksum = web3.to_checksum_address(token_in)
    token_out_checksum = web3.to_checksum_address(token_out)
    amount_out = uniswap3_quoter.functions.quoteExactInputSingle(
        token_in_checksum,  # tokenIn
        token_out_checksum,  # tokenOut
        fee,  # fee
        amount_in,  # amountIn
        0  # sqrtPriceLimitX96
    ).call()
    amount_out = [amount_in/10**18, amount_out/10**18]
    return amount_out

def get_amount_out_curve(contract, amount_in):
    amount_out = contract.functions.get_dy(0, 1, amount_in).call()
    amount_out = [amount_in / 10 ** 18, amount_out / 10 ** 18]
    return amount_out

def get_quote(amount_in, token_in, token_out, protocol, version, fee):
    token_in_checksum = web3.to_checksum_address(token_in)
    token_out_checksum = web3.to_checksum_address(token_out)
    amount_out = []
    if protocol == "uniswap":
        if version == "v2":
            amount_out = uniswap2_router.functions.getAmountsOut(web3.to_wei(amount_in, 'ether'), [token_in_checksum, token_out_checksum]).call()
            amount_out = [amount_out[0] / 1e18, amount_out[1] / 1e18]
        else:
            amount_out = get_amount_out_v3(web3.to_wei(amount_in, 'ether'), token_in_checksum, token_out_checksum, fee)
    elif protocol == "curve":
        contract = oeth_contract
        amount_out = get_amount_out_curve(contract, web3.to_wei(amount_in, 'ether'))
    price = amount_out[1]/amount_out[0]
    return amount_out, price



def get_no_slippage_quote(token_in, token_out, protocol, version, fee):
    result = get_quote(0.00001, token_in, token_out, protocol, version, fee)
    return result



def binary_search_for_slippage(token_in, token_out, protocol, version, fee, target_slippage, tolerance):
    no_slippage_quote = get_no_slippage_quote(token_in, token_out, protocol, version, fee)
    lower_bound = 1
    upper_bound = 50000  # You might need to adjust this depending on the token
    mid_point = (upper_bound + lower_bound) / 2

    current_quote = get_quote(mid_point, token_in, token_out, protocol, version, fee)
    current_slippage = (no_slippage_quote[1] - current_quote[1]) / no_slippage_quote[1]

    while (abs(current_slippage - target_slippage) > target_slippage * tolerance) and ( abs(upper_bound - mid_point) > 1 ) and ( abs(lower_bound - mid_point) > 0.01 ):
        if current_slippage > target_slippage:
            upper_bound = mid_point
        else:
            lower_bound = mid_point

        mid_point = (upper_bound + lower_bound) / 2
        current_quote = get_quote(mid_point, token_in, token_out, protocol, version, fee)
        current_slippage = (no_slippage_quote[1] - current_quote[1]) / no_slippage_quote[1]
        print("try swap size:" +str(mid_point) + "; slippage:" +str(current_slippage))
    return mid_point




